mapper.select:
SELECT id, role_name AS roleName, role_desc AS roleDesc, personality, background, user_id AS userId, avatar_url AS avatarUrl, create_time AS createTime, update_time AS updateTime, is_builtin AS isBuiltin, voice FROM roles WHERE
id = #{roleId}
role_name LIKE CONCAT('%', #{roleName}, '%')
user_id = #{userId}
